
public class froloop 
{

	public static void main(String[] args) 
	{
		// TODO Auto-generated method stub
		//Display number from 1-5 and addition of numbers
		
		int n=5;
		int sum=0;
		
		for(int i=1;i<=n;i++)
		{
			sum=sum+n;
			System.out.println(i);
			System.out.println("Sum="+sum);
		}
		
	}

}
